function getData() {
  fetch("https://dummyjson.com/products/1")
    .then((t) => t.json())
    .then((t) => console.log(t))
    .catch((t) => console.log(t));
}
async function getData2() {
  try {
    const t = await fetch("https://dummyjson.com/products/2"),
      n = await t.json();
    (divEl.innerText = n.description), console.log(n);
  } catch (t) {
    console.log(t);
  }
}
getData();
var divEl = document.getElementById("div"),
  btn = document.getElementById("btn");
btn.addEventListener("click", getData2), getData2();
